mapper.select:
SELECT

FROM
sys_menu


        OR name = #{queryText}




        ORDER BY ${sort} ${order}


        ORDER BY sys_menu.id DESC



    LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM
sys_menu


        AND name = #{queryText}

mapper.update:
UPDATE sys_menu
name = #{name}, type = #{type}, url = #{url}, permission = #{permission}, ico = #{ico}, parent_id = #{parentId}, sequence = #{sequence}, status = #{status}, modify_time = now()
WHERE id = #{id}

mapper.select:
SELECT DISTINCT
FROM sys_menu JOIN sys_role_menu um ON um.menu_id = sys_menu.id JOIN sys_role ro ON um.role_id = ro.id WHERE ro.key IN
#{roleKey}
