mapper.select:
SELECT

FROM
sys_role


        OR name = #{queryText}
        OR key = #{queryText}
        OR description = #{queryText}




        ORDER BY ${sort} ${order}


        ORDER BY sys_role.id DESC



    LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM
sys_role


        AND name = #{queryText}
        OR key = #{queryText}
        OR description = #{queryText}

mapper.update:
UPDATE sys_role
name = #{name}, `key` = #{key}, description = #{description}, status = #{status}, modify_user = #{modifyUser}, modify_time = now()
WHERE id = #{id}
